// ─── TOAST ───────────────────────────────────────────────────────────────────
let _toast = null;
const showToast = (msg, type = "success") => _toast?.(msg, type);

const ToastContainer = () => {
  const [toasts, setToasts] = useState([]);
  _toast = (msg, type) => {
    const id = Date.now();
    setToasts(p => [...p, { id, msg, type }]);
    setTimeout(() => setToasts(p => p.filter(t => t.id !== id)), 3500);
  };
  return (
    <div style={{ position:"fixed", bottom:24, right:24, zIndex:9999, display:"flex", flexDirection:"column", gap:8 }}>
      {toasts.map(t => (
        <div key={t.id} style={{
          background: t.type==="error" ? "#EF4444" : t.type==="warning" ? "#F59E0B" : T.success,
          color:"#fff", padding:"12px 20px", borderRadius:12, boxShadow:"0 8px 32px rgba(0,0,0,0.4)",
          fontSize:14, fontWeight:500, display:"flex", alignItems:"center", gap:8, minWidth:260,
          animation:"slideIn 0.3s ease"
        }}>
          {t.type==="error" ? "✗" : "✓"} {t.msg}
        </div>
      ))}
    </div>
  );
};

