const Btn = ({ children, variant="primary", size="md", style:s={}, ...p }) => {
  const base = { border:"none", cursor:p.disabled?"not-allowed":"pointer", fontWeight:600, borderRadius:10, display:"inline-flex", alignItems:"center", gap:6, transition:"all 0.2s", opacity:p.disabled?0.5:1 };
  const sz = size==="sm" ? { padding:"7px 14px", fontSize:13 } : size==="lg" ? { padding:"13px 28px", fontSize:15 } : { padding:"10px 20px", fontSize:14 };
  const vs = {
    primary: { background:T.grad, color:"#fff", boxShadow:"0 4px 15px rgba(99,102,241,0.3)" },
    secondary: { background:T.bg3, color:T.text, border:`1px solid ${T.border}` },
    ghost: { background:"transparent", color:T.text2, border:`1px solid ${T.border2}` },
    danger: { background:"#EF444420", color:"#EF4444", border:"1px solid #EF444440" },
    success: { background:"#10B98120", color:"#10B981", border:"1px solid #10B98140" },
  };
  return <button {...p} style={{ ...base, ...sz, ...vs[variant], ...s }}>{children}</button>;
};

