// ─── LOGIN ───────────────────────────────────────────────────────────────────
const LoginPage = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [pw, setPw] = useState("");
  const [loading, setLoading] = useState(false);

  const submit = async () => {
    if (!email || !pw) return;
    setLoading(true);
    const { data, error } = await supabase.auth.signInWithPassword({ email, password: pw });
    if (error) { showToast(error.message, "error"); setLoading(false); return; }
    const { data: prof } = await supabase.from("profiles").select("*").eq("id", data.user.id).single();
    onLogin(data.user, prof);
    setLoading(false);
  };

  return (
    <div style={{ minHeight:"100vh", background:T.bg, display:"flex", alignItems:"center", justifyContent:"center", fontFamily:"'Inter',system-ui,sans-serif" }}>
      <div style={{ position:"absolute", inset:0, overflow:"hidden", pointerEvents:"none" }}>
        <div style={{ position:"absolute", width:600, height:600, borderRadius:"50%", background:"radial-gradient(circle,rgba(99,102,241,0.12),transparent 70%)", top:-200, right:-100 }}/>
        <div style={{ position:"absolute", width:500, height:500, borderRadius:"50%", background:"radial-gradient(circle,rgba(14,165,233,0.08),transparent 70%)", bottom:-100, left:-100 }}/>
      </div>
      <div style={{ position:"relative", background:T.card, border:`1px solid ${T.border}`, borderRadius:20, padding:40, width:"100%", maxWidth:400, boxShadow:"0 24px 80px rgba(0,0,0,0.5)" }}>
        <div style={{ textAlign:"center", marginBottom:32 }}>
          <img src={LOGO_SRC} alt="ConsultFlow" style={{ width:110, height:110, objectFit:"contain", margin:"0 auto 12px", display:"block", filter:"drop-shadow(0 4px 20px rgba(99,102,241,0.3))" }}/>
          <h1 style={{ margin:0, fontSize:24, fontWeight:800, background:T.grad, WebkitBackgroundClip:"text", WebkitTextFillColor:"transparent" }}>ConsultFlow</h1>
          <p style={{ margin:"6px 0 0", color:T.text3, fontSize:14 }}>Danışmanlık Yönetim Platformu</p>
        </div>
        <Inp label="E-posta" type="email" value={email} onChange={e=>setEmail(e.target.value)} placeholder="ornek@firma.com" onKeyDown={e=>e.key==="Enter"&&submit()} />
        <Inp label="Şifre" type="password" value={pw} onChange={e=>setPw(e.target.value)} placeholder="••••••••" onKeyDown={e=>e.key==="Enter"&&submit()} />
        <Btn onClick={submit} disabled={loading} style={{ width:"100%", justifyContent:"center", marginTop:8 }} size="lg">
          {loading ? "Giriş yapılıyor..." : "Giriş Yap"}
        </Btn>
      </div>
    </div>
  );
};

